import React from 'react';
import { BrowserRouter as Router, Route, Link, Redirect } from 'react-router-dom';

import RegisterForm from './components/RegisterForm';
import LoginForm from './components/LoginForm';
import DonationForm from './components/DonationForm';
import DonationSecondForm from './components/DonationSecondForm';
import DonorPage from './components/DonorPage';
import BloodRequestForm from './components/BloodRequestForm';
import Header from './containers/Header';

import { connect } from 'react-redux';
import * as bs from 'bootstrap/dist/css/bootstrap.min.css'
import  '../src/style.css';

import { Container, Row, Col, Navbar, Nav, NavDropdown, Carousel, Image } from 'react-bootstrap';

class App extends React.Component {

  constructor(props) {
    super(props);
  }

  render() {
      return (
          <Router>
              <div className="App">
                <div clasName="header">
                <header>
                  <Navbar bg="light" expand="lg">
                      <Navbar.Brand href="/">Blood-Sharing</Navbar.Brand>
                      <Navbar.Toggle aria-controls="basic-navbar-nav" />
                      <Navbar.Collapse id="basic-navbar-nav">
                          <Nav className="mr-auto">
                              <Nav.Link href="/">Home</Nav.Link>
                              <Nav.Link href="/donate"></Nav.Link>

                              {this.props.currentUser.id !== undefined && (
                                <React.Fragment>
                                  <Link to="/donate">Donate</Link> | <Link to="/blood/request">Request</Link>
                                </React.Fragment>
                              )}
                              <Nav.Link href="/donate/request">Request</Nav.Link>
                              <Nav.Link href="/donor">Donor</Nav.Link>
                          </Nav>

                          <Nav className="ml-auto">
                              <Nav.Link href="/register">Register</Nav.Link>
                              <Nav.Link href="/login">Login</Nav.Link>
                          </Nav>

                      </Navbar.Collapse>
                  </Navbar>
              </header>
                </div>

                  <Link to="/">Home</Link> | <Link to="/register">Register</Link> | <Link to="/login">Login</Link> 


                  {this.props.currentUser.id !== undefined && (
                    <React.Fragment>
                      | <Link to="/donate">Donate</Link> | <Link to="/blood/request">Request</Link>
                    </React.Fragment>
                  )}
              </div>

              <Route path="/register" component={RegisterForm} />
              <Route path="/login" component={LoginForm} />
              <Route exact path="/donate" component={DonationForm} />
              <Route exact path="/donate/next" component={DonationSecondForm} />
              <Route path="/blood/request" component={BloodRequestForm} />
              <Route exact path="/donor" component={DonorPage} />
          </Router>
      )
  }
} 

const mapStateToProps  = state => ({ 
  currentUser : state.currentUser
})

export default connect (mapStateToProps) (App)
