import React from 'react';
import { BrowserRouter as Redirect } from "react-router-dom";

import { connect } from 'react-redux';
import { loginUserEvent,fetchUsers } from '../../../actions/userActions';
import firebase from '../../../utilites/firebase';

import { Container, Row, Col, Navbar, Nav, NavDropdown, NavItem, Form, Button, p, TabPane, TabContent } from 'react-bootstrap';
import regImg from '../../../assets/images/reg_img.jpg';

class LoginForm extends React.Component {

	constructor(props) {
		super(props);
		this.username = React.createRef();
		this.password = React.createRef();
		this.props.fetchUsers();

		this.state = {
			redirectToReferrer : false,
			error: {},
            //login: false
		};
	}

	successLogin = () => {
        this.setState({
            redirectToReferrer: true
        })
    }
    errorLogin = (error) => {
        this.setState({
            error: error
        })
    }

	handleOnSubmit = (event) => {
		event.preventDefault();

		const email = this.username.current.value;
		const password = this.password.current.value;
		// const currentUser = this.props.users.find( user => user.username == username );

		// if(currentUser){
		// 	this.props.loginUserEvent(
		// 	  	currentUser,
		// 	  	() => {
		// 			this.setState({ redirectToReferrer: true });
		// 	  	}
		// 	);
		// }
		// else{
		// 	alert("Wrong password");
		// }

		this.props.loginUserEvent(
            this.username.current.value,
            this.password.current.value,
            this.successLogin,
            this.errorLogin
        );
	}

	render() {
		let { from } = this.props.location.state || { from: { pathname:"/"} };
		let { redirectToReferrer } = this.state;

		if(redirectToReferrer) return <Redirect to={from} />

		return (
			<div className="registerSession login-form">
				<Container>
					<Row>
						<Col md={ 5 } className="p-0">
							<div class="reg_img">
								{/* <img src={regImg}/> */}
							</div>
						</Col>

					<Col md={ 6 } className="regform border p-0">
							<Row>
								<Col className="text-center mt-5">
									<h2>Login Here</h2>
								</Col>
							</Row>
							<div className="form_field">
								<div className="registerSession mt-5">
									<Form className="form p-4" onSubmit={this.handleOnSubmit}>
										<div className="form-group">
											<Row>
												<Col className="text-right">
													<label for="username">Name</label>
												</Col>
												<Col md={ 9 }>
													<input id="username" className="form-control" ref={this.username} required />
												</Col>
											</Row>
										</div>
										<div className="form-group mb-4">
											<Row>
												<Col className="text-right">
													<label for="password">Password</label>
												</Col>

												<Col md={ 9 }>
													<input type="password" className="form-control" id="password"  ref={this.password} required/>
												</Col>
											</Row>
										</div>

										<div className="form-bottom text-right">
											<Button type="submit">Login</Button>
										</div>
									</Form>

								</div>
							</div>
						</Col>
						<Col></Col>
					</Row>
				</Container>
			</div>
		  );
	}
}

const mapStateToProps = (state) => ({
	users : state.users  
});

const mapDispatchToProps = {
	loginUserEvent,
	fetchUsers
}

export default connect(mapStateToProps,mapDispatchToProps)(LoginForm)
