import React from "react";
import { BrowserRouter as Link,Redirect } from "react-router-dom";

import { connect } from "react-redux";
import { insertDonor } from "../../actions/donorActions";

export default class DonationSecondForm extends React.Component {
    constructor(props) {
        super(props);
        this.lastDonationDate = React.createRef();
        this.weight = React.createRef();

        this.state = {
            completeDonationSecondForm: false
        };
    }

    handleOnSubmit = event => {
        event.preventDefault();
        // this.props.insertDonor(
        //     {
        //         name: this.name.current.value,
        //         phone: this.phone.current.value,
        //         location: this.location.current.value,
        //         blood_type: this.blood_type.current.value,
        //         dob: this.dob.current.value,
        //         gender: this.gender.current.value,
        //     },
        //     () => {
        //     this.setState({
        //         completeDonationForm: true
        //     });
        // });    

        this.setState({
            completeDonationSecondForm: true
        });


    };

    render() {
        console.log(this.state.completeDonationSecondForm);
        if (this.state.completeDonationSecondForm) 
        return <Redirect to='/donor' />;

        return (
            <form onSubmit={this.handleOnSubmit}>
                <label htmlFor="lastDonationDate">Last Donation Date : </label>
                <input id="lastDonationDate" ref={this.lastDonationDate} required />
                <br />
                <label htmlFor="weight">Weight : </label>
                <input id="weight" ref={this.weight} required />
                <br />

                <div>
                    <Link to="/"><button type="submit">Cancel</button></Link>
                    <button type="submit">Yes, I' Done</button>
                </div>               

            </form>
        );
    }
}

