import React from "react";
import { BrowserRouter as Redirect, Router, Route, Link } from "react-router-dom";

import { connect } from "react-redux";
import { insertUser } from "../../actions/userActions";

class RegisterForm extends React.Component {
    constructor(props) {
        super(props);
        this.name = React.createRef();
        this.phone = React.createRef();
        this.city = React.createRef();
        this.dob = React.createRef();
        this.weight = React.createRef();
        this.blood_type = React.createRef();

        this.state = {
            completeRegister: false
        };
    }

    handleOnSubmit = event => {
        event.preventDefault();
        this.props.insertUser(
            {
                name: this.name.current.value,
                phone: this.phone.current.value,
                city: this.city.current.value,
                dob: this.dob.current.value,
                weight: this.weight.current.value,
                blood_type: this.blood_type.current.value
            },
            () => {
            this.setState({
                completeRegister: true
            });
        });    
    };

    render() {
        console.log(this.state.completeRegister);
        if (this.state.completeRegister) return <Redirect to="/" />;

        return (
            <form onSubmit={this.handleOnSubmit}>
                <label htmlFor="name">Name : </label>
                <input id="name" ref={this.name} required />
                <br />
                <label htmlFor="phone">Phone Number : </label>
                <input id="phone" ref={this.phone} required />
                <br />
                <label htmlFor="city">City : </label>
                <input id="city" ref={this.city} required />
                <br />
                <label htmlFor="dob">Date of Birth : </label>
                <input id="dob" ref={this.dob} required />
                <br />
                <label htmlFor="weight">Weight : </label>
                <input id="weight" ref={this.weight} required />
                <br />
                <label htmlFor="blood_type">Blood Type :</label>
                <input id="blood_type" ref={this.blood_type} required />
                <br />
                <button type="submit">Register</button>
            </form>
        );
    }
}

const mapDispatchToProps = {
    insertUser
}

export default connect(null, mapDispatchToProps)(RegisterForm);
