import React from "react";
import { BrowserRouter as  Redirect } from "react-router-dom";

import { connect } from "react-redux";
import { insertBloodRequest } from "../../../actions/bloodRequestActions";

import { Container, Row, Col, Navbar, Nav, NavDropdown, NavItem, Form, Button, p, TabPane, TabContent } from 'react-bootstrap';
import regImg from '../../../assets/images/reg_img.jpg';

class BloodRequestForm extends React.Component {
    constructor(props) {
        super(props);
        this.bloodCount = React.createRef();
        this.name = React.createRef();
        this.phone = React.createRef();
        this.reason = React.createRef();        

        this.state = {
            bloodType: 'A',
            location: '500-bed Specialty Hospital, Yangon',
            status: 'urgent',
            completeBloodRequest: false
        };
    }

    bloodTypeOnChange = (event) => {
        this.setState({
            bloodType: event.target.value
        })
        console.log(event.target.value);
    }

    locationOnChange = (event) => {
        this.setState({
            location: event.target.value
        })
    }

    statusOnChange = (event) => {
        this.setState({
            status: event.target.value
        })
    }

    handleOnSubmit = event => {
        event.preventDefault();

        const newBloodRequest = {
            bloodCount: this.bloodCount.current.value,
            bloodType: this.state.bloodType,
            location: this.state.location,
            numOfDonor: '',
            numOfViewer: '',
            name: this.name.current.value,
            phone: this.phone.current.value,
            reason: this.reason.current.value,
            requestDate: new Date(),
            uid: this.props.currentUser.uid,
            username: this.props.currentUser.email,                     
        };

        this.props.insertBloodRequest(newBloodRequest); 

        this.setState({
            completeBloodRequest: true
        });   
    };

    render() {
        //console.log(this.state.completeBloodRequest);
        if (this.state.completeBloodRequest) return <Redirect to="/allrequestlist" />;

        return (

            <div className="registerSession mt-5">
                <Container>
                    <Row>
                        {/* <Col md={ 5 } className="p-0">
                            <div class="reg_img">
                                <img src={regImg}/>
                            </div>
                        </Col> */}
                        <Col></Col>

                        <Col md={ 9 } className="regform text-left">
                            <Row>
                                <Col md={ 10 } className="text-center mt-4">
                                    <h2>Request For Blood</h2>
                                </Col>
                            </Row>                        

                            <div className="form_field">
                                <div className="registerSession mt-5">
                                    <Form className="form" onSubmit={this.handleOnSubmit}>
                                        <div className="form-group">
                                            <Row>
                                                <Col md={ 4 } className="text-right">
                                                    <label for="name">Name</label>
                                                </Col>
                                                <Col md={ 6 }>
                                                    <input id="name" className="form-control" ref={this.name} required />
                                                </Col>

                                            </Row>
                                        </div>

                                        <div className="form-group">
                                            <Row>
                                                <Col md={ 4 } className="text-right">
                                                    <label for="phone">Phone No</label>
                                                </Col>
                                                <Col md={ 6 }>
                                                    <input id="phone" className="form-control" ref={this.phone} required />
                                                </Col>
                                            </Row>
                                        </div>

                                        <div className="form-group">
                                            <Row>
                                                <Col md={ 4 } className="text-right mt-4">
                                                    <label for="bloodType">Please select your blood group</label>
                                                </Col>
                                                <Col md={ 6 }>
                                                    <Row className="pl-3 blood-types">
                                                        <Button className="form-control btnblood-type">A +</Button>
                                                        <Button className="form-control btnblood-type">A -</Button>
                                                        <Button className="form-control btnblood-type">B +</Button>
                                                        <Button className="form-control btnblood-type">B -</Button>
                                                    </Row>

                                                    <Row className="pl-3 blood-types">
                                                        <Button className="form-control btnblood-type">AB +</Button>
                                                        <Button className="form-control btnblood-type">AB -</Button>
                                                        <Button className="form-control btnblood-type">O +</Button>
                                                        <Button className="form-control btnblood-type">O -</Button>
                                                    </Row>
                                                    {/* <Row className="pl-3">
                                                        <input type="radio" value="A" checked={this.state.bloodType === "A"}
                                                            onChange={this.bloodTypeOnChange} /><label className="mr-3" for="A">A</label>

                                                        <input type="radio" value="A+" checked={this.state.bloodType === "A+"}
                                                            onChange={this.bloodTypeOnChange} /><label className="mr-3" for="A +">A +</label>

                                                        <input type="radio" value="B" checked={this.state.bloodType === "B"}
                                                            onChange={this.bloodTypeOnChange} /><label className="mr-3" for="B">B</label>

                                                        <input type="radio" value="B+" checked={this.state.bloodType === "B+"}
                                                            onChange={this.bloodTypeOnChange} /><label className="mr-3" for="B +">B +</label>
                                                    </Row>

                                                    <Row className="pl-3">
                                                        <input type="radio" value="O" checked={this.state.bloodType === "O"}
                                                            onChange={this.bloodTypeOnChange} /><label className="mr-3" for="O">O</label>

                                                        <input type="radio" value="O+" checked={this.state.bloodType === "O+"}
                                                            onChange={this.bloodTypeOnChange} /><label className="mr-3" for="O+">O+</label>

                                                        <input type="radio" value="AB" checked={this.state.bloodType === "AB"}
                                                            onChange={this.genderOnChange} /><label className="mr-2" for="AB">AB</label>

                                                        <input type="radio" value="AB+" checked={this.state.bloodType === "AB+"}
                                                            onChange={this.bloodTypeOnChange} /><label className="mr-3" for="AB+">AB+</label>
                                                    </Row>   */}           
                                                </Col>
                                            </Row>
                                        </div>

                                        <div className="form-group">
                                            <Row>
                                                <Col md={ 4 } className="text-right">
                                                    <label for="bloodcount">Blood Count</label>
                                                </Col>
                                                <Col md={ 6 }>
                                                    <input type="number" className="form-control w-70" id="bloodCount" ref={this.bloodCount} required />
                                                </Col>
                                            </Row>
                                        </div>                                        

                                        <div className="form-group">
                                            <Row>            
                                                <Col md={ 4 } className="text-right">
                                                    <label for="location">Location</label>
                                                </Col>
                                                <Col md={ 6 }>
                                                    <select id="location" className="form-control" onChange={this.locationOnChange}>
                                                        <option value="500-bed Specialty Hospital, Yangon">500-bed Specialty Hospital, Yangon</option>
                                                        <option value="Defence Services General Hospital (1000-bed)">Defence Services General Hospital (1000-bed)</option>
                                                        <option value="Defence Services Orthopaedic Hospital (500-Bed)">Defence Services Orthopaedic Hospital (500-Bed)</option>
                                                        <option value="Defence Services Obstetric, Gynaecological and Paediatric Hospital">Defence Services Obstetric, Gynaecological and Paediatric Hospital</option>

                                                    </select>
                                                </Col>
                                            </Row>
                                        </div>

                                        <div className="form-group">
                                            <Row>
                                                <Col md={ 4 } className="text-right">
                                                    <label for="reason">Reason</label>
                                                </Col>
                                                <Col md={ 6 }>
                                                    <textarea id="phone" cols="10" rows="5" className="form-control" ref={this.reason} required />
                                                </Col>
                                            </Row>
                                        </div>

                                        <div className="form-group">
                                            <Row>
                                                <Col md={ 4 } className="text-right">
                                                    <label for="status">Status</label>
                                                </Col>
                                                <Col md={ 6 } className="text-left">

                                                    <input type="radio" value="urgent" checked={this.state.status === "urgent"}
                                                            onChange={this.statusOnChange} /><label className="mr-3 ml-2" for="Urgent">Urgent</label>

                                                    <input type="radio" value="normal" checked={this.state.status === "normal"}
                                                    onChange={this.statusOnChange} /><label className="mr-3 ml-2" for="Normal">Normal</label>
                                                </Col>
                                            </Row>
                                        </div>

                                        <div className="form-bottom">
                                            <Row>
                                                <Col md={ 6 } className="text-right">
                                                    <Button type="submit">Submit</Button>
                                                </Col>
                                            </Row>

                                        </div>
                                    </Form>
                                </div>
                            </div>
                        </Col>
                        <Col></Col>
                    </Row>
                </Container>
            </div>            
        );
    }
}

