import React from 'react';
import { BrowserRouter as Router, Route, Link, Redirect } from 'react-router-dom';

import RegisterForm from './components/RegisterForm';
import LoginForm from './components/LoginForm';
import DonationForm from './components/DonationForm';
import DonationSecondForm from './components/DonationSecondForm';
import DonorPage from './components/DonorPage';
import BloodRequestForm from './components/BloodRequestForm';

import { connect } from 'react-redux';

class App extends React.Component {

  constructor(props) {
    super(props);
  }

  render() {
      return (
          <Router>
              <div className="App">
                  <h1>Welcome to Blood Sharing!</h1>
                  <Link to="/">Home</Link> | <Link to="/register">Register</Link> | <Link to="/login">Login</Link> 


                  {this.props.currentUser.id !== undefined && (
                    <React.Fragment>
                      | <Link to="/donate">Donate</Link> | <Link to="/blood/request">Request</Link>
                    </React.Fragment>
                  )}
              </div>

              <Route path="/register" component={RegisterForm} />
              <Route path="/login" component={LoginForm} />
              <Route exact path="/donate" component={DonationForm} />
              <Route exact path="/donate/next" component={DonationSecondForm} />
              <Route path="/blood/request" component={BloodRequestForm} />
              <Route exact path="/donor" component={DonorPage} />
          </Router>
      )
  }
} 

const mapStateToProps  = state => ({ 
  currentUser : state.currentUser
})

export default connect (mapStateToProps) (App)
