import {retrieveData,storeData} from "../utilites/localStorage";

export const insertBloodRequest = (bloodRequest) => dispatch => {
    const bloodRequests = retrieveData("bloodRequests");
    const mapped_array = bloodRequests.map( i => parseInt(i.id));
    const max_id = Math.max(...mapped_array);
    bloodRequest.id = max_id + 1;
    bloodRequests.push(bloodRequest);
    storeData("bloodRequests", bloodRequests);

    dispatch({
        type: 'ADD_NEW_BLOOD_REQUEST',
        data : bloodRequest
    });
};
