import React from "react";
import { BrowserRouter as Redirect, Router, Route, Link } from "react-router-dom";

import { connect } from "react-redux";
import { insertBloodRequest } from "../../actions/bloodRequestActions";
import '../../assets/css/test.css';

class BloodRequestForm extends React.Component {
    constructor(props) {
        super(props);
        this.name = React.createRef();
        this.phone = React.createRef();
        this.blood_type = React.createRef();
        this.city = React.createRef();
        this.reason = React.createRef();        

        this.state = {
            completeBloodRequest: false
        };
    }

    handleOnSubmit = event => {
        event.preventDefault();
        this.props.insertBloodRequest(
            {
                name: this.name.current.value,
                phone: this.phone.current.value,
                blood_type: this.blood_type.current.value,
                city: this.city.current.value,
                reason: this.reason.current.value,
            },
            () => {
            this.setState({
                completeBloodRequest: true
            });
        });    
    };

    render() {
        console.log(this.state.completeBloodRequest);
        if (this.state.completeBloodRequest) return <Redirect to="/" />;

        return (
            <form onSubmit={this.handleOnSubmit}>
                <label htmlFor="name">Name : </label>
                <input id="name" ref={this.name} required />
                <br />
                <label htmlFor="phone">Phone Number : </label>
                <input id="phone" ref={this.phone} required />
                <br />
                <label htmlFor="blood_type">Blood Type :</label>
                <input id="blood_type" ref={this.blood_type} required />
                <br />
                <label htmlFor="city">City : </label>
                <input id="city" ref={this.city} required />
                <br />
                <label htmlFor="reason">Reason : </label>
                <input id="reason" ref={this.reason} required />
                <br />

                <button type="submit">Submit</button>
            </form>
        );
    }
}

const mapDispatchToProps = {
    insertBloodRequest
}

export default connect(null, mapDispatchToProps)(BloodRequestForm);
