import React from 'react';
import { BrowserRouter as Router, Route, Link, Redirect } from 'react-router-dom';

import RegisterForm from './components/auth/RegisterForm';
import LoginForm from './components/auth/LoginForm';
import DonationForm from './components/container/DonationForm';
import DonationSecondForm from './components/container/DonationSecondForm';
import BloodRequestForm from './components/container/BloodRequestForm';
import BloodRequestList from './components/container/BloodRequestList';
import BloodRequestDetail from './components/container/BloodRequestDetail';

import { connect } from 'react-redux';

class App extends React.Component {

  constructor(props) {
    super(props);
  }

  render() {
      return (
          <Router>
              <div className="App">
                  <h1>Welcome to Blood Sharing!</h1>
                  <Link to="/">Home</Link> 

                  {this.props.currentUser.uid == undefined && (
                    <React.Fragment>
                      | <Link to="/login">Login</Link>
                      | <Link to="/register">Register</Link>
                    </React.Fragment>
                  )} 

                  {this.props.currentUser.uid !== undefined && (
                    <React.Fragment>
                      | <Link to="/donate">Donate</Link> 
                      | <Link to="/request">Request</Link>
                      | <Link to="/bloodRequests">Request List</Link>
                    </React.Fragment>
                  )}
              </div>

              <Route path="/register" component={RegisterForm} />
              <Route path="/login" component={LoginForm} />
              <Route exact path="/donate" component={DonationForm} />
              <Route exact path="/donate/next" component={DonationSecondForm} />
              <Route exact path="/donor" component={BloodRequestList} />
              <Route exact path="/request" component={BloodRequestForm} />
              <Route exact path="/bloodRequests" component={BloodRequestList} />
              <Route exact path="/bloodRequest/:id" component={BloodRequestDetail} />
          </Router>

      )
  }
}

const mapStateToProps  = state => ({ 
  currentUser : state.currentUser
})

export default connect (mapStateToProps) (App)
