import React from "react";
import { BrowserRouter as  Router, Route, Link,Redirect } from "react-router-dom";

import { connect } from 'react-redux';
import { fetchBloodRequests } from '../../../actions/bloodRequestActions';

class UserBloodRequest extends React.Component {
    constructor(props) {
        super(props);
        this.props.fetchBloodRequests();

        this.state = {
			redirectToReferrer : false
		};
    }

    render() {
        const bloodRequests = this.props.bloodRequests;
        const uid = this.props.currentUser.uid;

        const user_request = bloodRequests.find( item => item.uid == uid);
        console.log(user_request);

        if(user_request !== undefined) {
            return (
                <React.Fragment>
                    <p>{user_request.name}</p>
                    <p>{user_request.bloodType}</p>
                    <p>{user_request.location}</p>
                    <p>{ Date(user_request.requestedDate) }</p>
                </React.Fragment>
            )
        }

        return (
            <p>No Request</p>
        )

    }
}

const mapStateToProps = state => ({
    currentUser : state.currentUser,
    bloodRequests: state.bloodRequests,
  });

  const mapDispatchToProps = {
    fetchBloodRequests,
  }

  export default connect(mapStateToProps,mapDispatchToProps)(UserBloodRequest);
