import React from "react";
import { BrowserRouter as Router, Route, Link, Redirect} from "react-router-dom";

import { fetchDonors } from '../../../actions/donorActions';

import BloodRequestList from '../BloodRequestList';

import { connect } from "react-redux";
import { Col } from "react-bootstrap";

class DonorPage extends React.Component {
    constructor(props) {
        super(props);
        this.props.fetchDonors();

    }
    render() {
        const uid = this.props.currentUser.uid;
        const donorList = this.props.donors;
        const donor = donorList.find( item => item.uid == uid);

        if(donor != undefined) {
            return <BloodRequestList />
        }
        else{
            return (
                <React.Fragment>
                    <Row>
                        <img src="https://media.gettyimages.com/photos/nurse-taking-blood-from-man-picture-id157732339?s=2048x2048"></img>
                    </Row>
                    <Row>
                        <Link to="/donate"><button>REGISTER AS DONOR</button></Link>
                    </Row>
                    <Row>
                        <h2>Things You Should Know before donation</h2>
                    </Row>

                    <Row>
                        <p>Scanfcode.com CODE WANTS TO BE SIMPLE is an initiative to help the upcoming programmers with the code.</p> 
                    </Row>

                    <Row>
                        <p>Scanfcode focuses on providing the most efficient code or snippets as the code wants to be simple.</p>
                    </Row>

                    <Row>
                        <p>Scanfcode.com CODE WANTS TO BE SIMPLE is an initiative to help the upcoming programmers with the code.</p> 
                    </Row>

                    <Row>
                        <p>Scanfcode focuses on providing the most efficient code or snippets as the code wants to be simple.</p>
                    </Row>       

                </React.Fragment>
            );
        }

    }
}

const mapStateToProps  = state => ({ 
    currentUser : state.currentUser,
    donors : state.donors
})

const mapDispatchToProps = {
    fetchDonors
}

export default connect (mapStateToProps, mapDispatchToProps) (DonorPage)
