import React from "react";
import { BrowserRouter as  Link,Redirect } from "react-router-dom";

import { connect } from "react-redux";
import { insertDonor } from "../../actions/donorActions";
import { updateUser } from "../../actions/userActions";


class DonationForm extends React.Component {
    constructor(props) {
        super(props);
        this.name = React.createRef();
        this.phone = React.createRef();
        this.dob = React.createRef();

        this.state = {
            gender: 'male',
            bloodType: 'A',
            location: 'Hlaing, Yangon',
            completeDonationForm: false
        };
    }

    genderOnChange = (event) => {
        this.setState({
            gender: event.target.value
        })
        console.log(event.target.value);
    }

    bloodTypeOnChange = (event) => {
        this.setState({
            bloodType: event.target.value
        })
        console.log(event.target.value);
    }

    locationOnChange = (event) => {
        this.setState({
            locaion: event.target.value
        })
    }


    handleOnSubmit = event => {
        event.preventDefault();
        // this.props.insertDonor(
        //     {
        //         name: this.name.current.value,
        //         phone: this.phone.current.value,
        //         location: this.location.current.value,
        //         blood_type: this.blood_type.current.value,
        //         dob: this.dob.current.value,
        //         gender: this.gender.current.value,
        //     },
        //     () => {
        //     this.setState({
        //         completeDonationForm: true
        //     });
        // });    

        this.setState({
            completeDonationForm: true
        });
    };

    render() {
        //console.log("render state",this.state.completeDonationForm);
        if (this.state.completeDonationForm) return <Redirect to="/donate/next" />;

        return (
            <form onSubmit={this.handleOnSubmit}>
                <label htmlFor="bloodType">Please select your blood group : </label><br/>
                <input type="radio" value="A" checked={this.state.bloodType === "A"}
                    onChange={this.bloodTypeOnChange} />
                A
                <input type="radio" value="A+" checked={this.state.bloodType === "A+"}
                    onChange={this.bloodTypeOnChange} />
                A+
                <input type="radio" value="B" checked={this.state.bloodType === "B"}
                    onChange={this.bloodTypeOnChange} />
                B
                <input type="radio" value="B+" checked={this.state.bloodType === "B+"}
                    onChange={this.bloodTypeOnChange} />
                B+
                <input type="radio" value="O" checked={this.state.bloodType === "O"}
                    onChange={this.bloodTypeOnChange} />
                O
                <input type="radio" value="O+" checked={this.state.bloodType === "O+"}
                    onChange={this.bloodTypeOnChange} />
                O+
                <input type="radio" value="AB" checked={this.state.bloodType === "AB"}
                    onChange={this.genderOnChange} />
                AB
                <input type="radio" value="AB+" checked={this.state.bloodType === "AB+"}
                    onChange={this.bloodTypeOnChange} />
                AB+
                <br /> 
                <label htmlFor="name">Name : </label>
                <input id="name" ref={this.name} required />
                <br />
                <label htmlFor="phone">Phone Number : </label>
                <input id="phone" ref={this.phone} required />
                <br />

                <label htmlFor="dob">Date of Birth : </label>
                <input id="dob" ref={this.dob} required />
                <br />
                <label htmlFor="gender">Gender : </label>
                <input type="radio" value="male" checked={this.state.gender === "male"}
                    onChange={this.genderOnChange} />
                Male
                <input type="radio" value="female" checked={this.state.gender === "female"}
                    onChange={this.genderOnChange} />
                Female
                <br /> 
                <label htmlFor="location">Location : </label>
                <select id="location" onChange={this.locationOnChange}>
                    <option value="Hlaing, Yangon">Hlaing, Yangon</option>
                    <option value="Kamaryut, Yangon">Kamaryut, Yangon</option>
                    <option value="Insein, Yangon">Insein, Yangon</option>
                </select>
                <br />
                <div>
                    <Link to="/"><button type="submit">Back</button></Link>
                    <button type="submit">Next</button>
                </div>               

            </form>
        );
    }
}

const mapStateToProps = state => ({
    currentUser : state.currentUser
})

const mapDispatchToProps = {
    insertDonor
}

export default connect(mapStateToProps, mapDispatchToProps)(DonationForm);
