import React from "react";
import { BrowserRouter as  Redirect } from "react-router-dom";

import { connect } from "react-redux";
import { insertBloodRequest } from "../../../actions/bloodRequestActions";

class BloodRequestForm extends React.Component {
    constructor(props) {
        super(props);
        this.bloodCount = React.createRef();
        this.name = React.createRef();
        this.phone = React.createRef();
        this.reason = React.createRef();        

        this.state = {
            bloodType: 'A',
            location: '500-bed Specialty Hospital, Yangon',
            status: 'urgent',
            completeBloodRequest: false
        };
    }

    bloodTypeOnChange = (event) => {
        this.setState({
            bloodType: event.target.value
        })
        console.log(event.target.value);
    }

    locationOnChange = (event) => {
        this.setState({
            location: event.target.value
        })
    }

    statusOnChange = (event) => {
        this.setState({
            status: event.target.value
        })
    }

    handleOnSubmit = event => {
        event.preventDefault();

        const newBloodRequest = {
            bloodCount: this.bloodCount.current.value,
            bloodType: this.state.bloodType,
            location: this.state.location,
            numOfDonor: '',
            numOfViewer: '',
            name: this.name.current.value,
            phone: this.phone.current.value,
            reason: this.reason.current.value,
            requestDate: new Date(),
            uid: this.props.currentUser.uid,
            username: this.props.currentUser.email,                     
        };

        this.props.insertBloodRequest(newBloodRequest); 

        this.setState({
            completeBloodRequest: true
        });   
    };

    render() {
        //console.log(this.state.completeBloodRequest);
        if (this.state.completeBloodRequest) return <Redirect to="/allrequestlist" />;

        return (
            <form onSubmit={this.handleOnSubmit}>
                <label htmlFor="name">Name : </label>
                <input id="name" ref={this.name} required />
                <br />
                <label htmlFor="phone">Phone Number : </label>
                <input id="phone" ref={this.phone} required />
                <br />
                <label htmlFor="bloodType">Please select your blood group : </label><br/>
                <input type="radio" value="A" checked={this.state.bloodType === "A"}
                    onChange={this.bloodTypeOnChange} />
                A
                <input type="radio" value="A+" checked={this.state.bloodType === "A+"}
                    onChange={this.bloodTypeOnChange} />
                A+
                <input type="radio" value="B" checked={this.state.bloodType === "B"}
                    onChange={this.bloodTypeOnChange} />
                B
                <input type="radio" value="B+" checked={this.state.bloodType === "B+"}
                    onChange={this.bloodTypeOnChange} />
                B+
                <input type="radio" value="O" checked={this.state.bloodType === "O"}
                    onChange={this.bloodTypeOnChange} />
                O
                <input type="radio" value="O+" checked={this.state.bloodType === "O+"}
                    onChange={this.bloodTypeOnChange} />
                O+
                <input type="radio" value="AB" checked={this.state.bloodType === "AB"}
                    onChange={this.genderOnChange} />
                AB
                <input type="radio" value="AB+" checked={this.state.bloodType === "AB+"}
                    onChange={this.bloodTypeOnChange} />
                AB+
                <br /> 

                <label htmlFor="bloodCount">Blood Count : </label>
                <input type="number" id="bloodCount" ref={this.bloodCount} required />
                <br />

                <label htmlFor="location">Location : </label>
                <select id="location" onChange={this.locationOnChange}>
                    <option value="500-bed Specialty Hospital, Yangon">500-bed Specialty Hospital, Yangon</option>
                    <option value="Defence Services General Hospital (1000-bed)">Defence Services General Hospital (1000-bed)</option>
                    <option value="Defence Services Orthopaedic Hospital (500-Bed)">Defence Services Orthopaedic Hospital (500-Bed)</option>
                    <option value="Defence Services Obstetric, Gynaecological and Paediatric Hospital">Defence Services Obstetric, Gynaecological and Paediatric Hospital</option>
                    <option value="East Yangon General Hospital">East Yangon General Hospital</option>
                    <option value="Insein General Hospital">Insein General Hospital</option>
                    <option value="New Yangon General Hospital">New Yangon General Hospital</option>
                    <option value="No.2 Military Hospital (500-bed)">No.2 Military Hospital (500-bed)</option>
                    <option value="North Okkalapa General Hospital">North Okkalapa General Hospital</option>
                    <option value="South Okkalapa Maternal and Child Hospital">South Okkalapa Maternal and Child Hospital</option>
                    <option value="Thingangyun Sanpya Hospital">Thingangyun Sanpya Hospital</option>
                    <option value="Universities Hospital[1]">Universities Hospital[1]</option>
                    <option value="Waibargi Hospital">Waibargi Hospital</option>
                    <option value="West Yangon General Hospital">West Yangon General Hospital</option>
                    <option value="Yangon Central Women's Hospital">Yangon Central Women's Hospital</option>
                    <option value="Yangon Children's Hospital">Yangon Children's Hospital</option>
                    <option value="Yangon ENT Hospital">Yangon ENT Hospital</option>
                    <option value="Yangon General Hospital">Yangon General Hospital</option>
                    <option value="Yangon Orthopaedic Hospital">Yangon Orthopaedic Hospital</option>
                    <option value="Yangon Workers' Hospital">Yangon Workers' Hospital</option>
                    <option value="Yangon Mental Health Hospital">Yangon Mental Health Hospital</option>
                    <option value="Yankin Children's Hospital">Yankin Children's Hospital</option>
                </select>
                <br />
                <label htmlFor="reason">Reason : </label>
                <input id="reason" ref={this.reason} required />
                <br />
                <input type="radio" value="urgent" checked={this.state.status === "urgent"}
                    onChange={this.statusOnChange} />
                Urgent
                <input type="radio" value="normal" checked={this.state.status === "normal"}
                    onChange={this.statusOnChange} />
                Normal
                <br /> 

                <button type="submit">Submit</button>
            </form>
        );
    }
}

const mapStateToProps = state => ({
    currentUser : state.currentUser
})

const mapDispatchToProps = {
    insertBloodRequest
}

export default connect(mapStateToProps, mapDispatchToProps)(BloodRequestForm);
