import React from 'react';
import { BrowserRouter as Redirect } from "react-router-dom";

import { connect } from 'react-redux';
import { loginUserEvent,fetchUsers } from '../../../actions/userActions';
import firebase from '../../../utilites/firebase';

class LoginForm extends React.Component {

	constructor(props) {
		super(props);
		this.username = React.createRef();
		this.password = React.createRef();
		this.props.fetchUsers();

		this.state = {
			redirectToReferrer : false,
			error: {},
            //login: false
		};
	}

	successLogin = () => {
        this.setState({
            redirectToReferrer: true
        })
    }
    errorLogin = (error) => {
        this.setState({
            error: error
        })
    }

	handleOnSubmit = (event) => {
		event.preventDefault();

		const email = this.username.current.value;
		const password = this.password.current.value;
		// const currentUser = this.props.users.find( user => user.username == username );

		// if(currentUser){
		// 	this.props.loginUserEvent(
		// 	  	currentUser,
		// 	  	() => {
		// 			this.setState({ redirectToReferrer: true });
		// 	  	}
		// 	);
		// }
		// else{
		// 	alert("Wrong password");
		// }

		this.props.loginUserEvent(
            this.username.current.value,
            this.password.current.value,
            this.successLogin,
            this.errorLogin
        );
	}

	render() {
		let { from } = this.props.location.state || { from: { pathname:"/"} };
		let { redirectToReferrer } = this.state;

		if(redirectToReferrer) return <Redirect to={from} />

		return (
			<form onSubmit={this.handleOnSubmit}>
			  <label htmlFor="username">Username : </label>
			  <input id="username" required ref={this.username} />
			  <br />
			  <label htmlFor="password">Password</label>
			  <input type="password" required id="password" ref={this.password} />
			  <br />
			  <button type="submit">Login</button>
			</form>
		  );
	}
}

const mapStateToProps = (state) => ({
	users : state.users  
});

const mapDispatchToProps = {
	loginUserEvent,
	fetchUsers
}

export default connect(mapStateToProps,mapDispatchToProps)(LoginForm)
