import {retrieveData,storeData} from "../utilites/localStorage";

export const insertDonor = (donor) => dispatch => {
    const donors = retrieveData("donors");
    const mapped_array = donors.map( i => parseInt(i.id));
    const max_id = Math.max(...mapped_array);
    donor.id = max_id + 1;
    donors.push(donor);
    storeData("donors", donors);

    dispatch({
        type: 'ADD_NEW_DONOR',
        data : donor
    });
};
