import React from "react";
import { BrowserRouter as  Link,Redirect } from "react-router-dom";

import { connect } from "react-redux";
import { insertDonor, fetchDonors } from "../../../actions/donorActions";
import { updateUser } from "../../../actions/userActions";

import { Container, Row, Col, Navbar, Nav, NavDropdown, NavItem, Form, Button, p, TabPane, TabContent } from 'react-bootstrap';
import regImg from '../../../assets/images/reg_img.jpg';

class DonationForm extends React.Component {
    constructor(props) {
        super(props);
        //this.props.fetchDonors();
        this.name = React.createRef();
        this.phone = React.createRef();
        this.dob = React.createRef();

        this.state = {
            gender: 'male',
            bloodType: 'A',
            location: 'Hlaing, Yangon',
            completeDonationForm: false
        };
    }

    genderOnChange = (event) => {
        this.setState({
            gender: event.target.value
        })
        console.log(event.target.value);
    }

    bloodTypeOnChange = (event) => {
        this.setState({
            bloodType: event.target.value
        })
        console.log(event.target.value);
    }

    locationOnChange = (event) => {
        this.setState({
            locaion: event.target.value
        })
    }


    handleOnSubmit = event => {
        event.preventDefault();

        const donor = {
            username: this.props.currentUser.email,
            uid: this.props.currentUser.uid,
        }

        //const donorData = this.props.donors.find( data => data.uid === donor.uid && data.username === donor.username);
        const user_id = this.props.currentUser.uid;
        const user = {
            name: this.name.current.value,
            phone: this.phone.current.value,
            bloodType: this.state.bloodType,
            dateOfBirth: this.dob.current.value,
            gender: this.state.gender,
            location: this.state.location,           
        }

        this.props.insertDonor(donor); 
        this.props.updateUser(user, user_id);

        this.setState({
            completeDonationForm: true
        });
    };

    render() {
        if (this.state.completeDonationForm) return <Redirect to="/donate/next" />;

        return (
            <div className="registerSession">
                <Container>
                    <Row>
                        <Col></Col>
                        <Col>  </Col>

                        <Col md={ 5 } className="regform p-0">
                            <Row>
                                <Col className="text-center ">
                                    <h2>Donation</h2>
                                </Col>
                            </Row>

                            <div className="form_field">
                                <div className="">
                                    <Form className="form p-4" onSubmit={this.handleOnSubmit}>
                                        <div className="form-group">
                                            <Row>
                                                <Col className="text-right">
                                                    <label for="name">Name</label>
                                                </Col>
                                                <Col md={ 9 }>
                                                    <input id="name" className="form-control" ref={this.name} required />
                                                </Col>
                                            </Row>
                                        </div>

                                        <div className="form-group">
                                            <Row>
                                                <Col className="text-right">
                                                    <label for="phone">Phone No</label>
                                                </Col>
                                                <Col md={ 9 }>
                                                    <input id="phone" className="form-control" ref={this.phone} required />
                                                </Col>
                                            </Row>
                                        </div>

                                        <div className="form-group">
                                            <Row>
                                                <Col className="text-right">
                                                    <label for="dob">Date of Birth</label>
                                                </Col>
                                                <Col md={ 9 }>
                                                    <input id="dob" className="form-control" ref={this.dob} required />
                                                </Col>
                                            </Row>
                                        </div>


                                        <div className="form-group">
                                            <Row>
                                                <Col className="text-right">
                                                    <label for="bloodType">Please select your blood group</label>
                                                </Col>
                                                <Col md={ 9 }>
                                                    <Row className="pl-3">
                                                        <input type="submit" value="A" checked={this.state.bloodType === "A"}
                                                            onChange={this.bloodTypeOnChange} className="btn_bloodType"/><label className="mr-3" for="A">A</label>

                                                        <input type="submit" value="A+" checked={this.state.bloodType === "A+"}
                                                            onChange={this.bloodTypeOnChange} className="btn_bloodType" /><label className="mr-3" for="A +">A +</label>

                                                        <input type="submit" value="B" checked={this.state.bloodType === "B"}
                                                            onChange={this.bloodTypeOnChange} className="btn_bloodType" /><label className="mr-3" for="B">B</label>

                                                        <input type="submit" value="B+" checked={this.state.bloodType === "B+"}
                                                            onChange={this.bloodTypeOnChange} className="btn_bloodType" /><label className="mr-3" for="B +">B +</label>

                                                            {/* <Button className="btn_bloodType">A +</Button>
                                                            <Button className="btn_bloodType">A </Button>
                                                            <Button className="btn_bloodType">B +</Button>
                                                            <Button className="btn_bloodType">B</Button> */}


                                                    </Row>


                                                    <Row className="pl-3">
                                                        <input type="radio" value="O" checked={this.state.bloodType === "O"}
                                                            onChange={this.bloodTypeOnChange} className="btn_bloodType" /><label className="mr-3" for="O">O</label>

                                                        <input type="radio" value="O+" checked={this.state.bloodType === "O+"}
                                                            onChange={this.bloodTypeOnChange} className="btn_bloodType" /><label className="mr-3" for="O+">O+</label>

                                                        <input type="radio" value="AB" checked={this.state.bloodType === "AB"}
                                                            onChange={this.genderOnChange} className="btn_bloodType" /><label className="mr-2" for="AB">AB</label>

                                                        <input type="radio" value="AB+" checked={this.state.bloodType === "AB+"}
                                                            onChange={this.bloodTypeOnChange} className="btn_bloodType" /><label className="mr-3" for="AB+">AB+</label>

                                                           {/*  <Button className="btn_bloodType">O +</Button>
                                                            <Button className="btn_bloodType">O </Button>
                                                            <Button className="btn_bloodType">AB +</Button>
                                                            <Button className="btn_bloodType">AB </Button> */}
                                                    </Row>                
                                                </Col>
                                            </Row>
                                        </div>

                                        <div className="form-group">
                                            <Row>            
                                                <Col className="text-right">
                                                    <label for="location">Location</label>
                                                </Col>
                                                <Col md={ 9 }>
                                                    <select id="location" className="form-control" onChange={this.locationOnChange}>
                                                        <option value="Hlaing, Yangon">Hlaing, Yangon</option>
                                                        <option value="Kamaryut, Yangon">Kamaryut, Yangon</option>
                                                        <option value="Insein, Yangon">Insein, Yangon</option>
                                                    </select>
                                                </Col>
                                            </Row>
                                        </div>
                                        <div className="form-bottom text-right">
                                            <Button type="submit" className="btnreg">Submit</Button>
                                        </div>
                                    </Form>
                                </div>
                            </div>
                        </Col>
                        <Col></Col>
                        <Col></Col>
                    </Row>
                </Container>
            </div>
        );
    }
}

const mapStateToProps = state => ({
    currentUser : state.currentUser,
    //donors: state.donors
})

const mapDispatchToProps = {
    insertDonor,
    updateUser,
    //fetchDonors
}

export default connect(mapStateToProps, mapDispatchToProps)(DonationForm);
